import React, { useEffect, useState } from "react";
import Table from "./Table";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faSearch, faTrash } from "@fortawesome/free-solid-svg-icons";

const AdminHome = () => {
  const [data, setData] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");

  useEffect(() => {
    fetch(`http://localhost:5050/getAllUser?search=${searchQuery}`, {
      method: "GET",
    })
      .then((res) => res.json())
      .then((data) => {
        console.log(data, "userData");
        setData(data.data);
      });
  }, [searchQuery]); // Make sure to include searchQuery as a dependency here

  const logout = () => {
    window.localStorage.clear();
    window.location.href = "./";
  };

  function handleSearch(e) {
    setSearchQuery(e.target.value);
  }

  return (
    <div className="bg-gray-200 min-h-screen flex justify-center items-center">
      <div className="bg-white rounded-lg shadow-md border border-gray-300 p-8 space-y-4 w-full max-w-lg overflow-hidden">
        <div>
          <input
            type="search"
            className="w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:border-blue-500"
            placeholder="Search"
            onChange={handleSearch}
          />
        </div>
        <div className="overflow-x-auto">
          <table className="w-full table-auto">
            <thead>
              <tr>
                <th className="px-4 py-2">User ID</th>
                <th className="px-4 py-2">Name</th>
                <th className="px-4 py-2">Email</th>
                {/* Add more table headers if needed */}
              </tr>
            </thead>
            <tbody>
              {data.map((userData) => (
                <Table key={userData._id} userData={userData} />
              ))}
            </tbody>
          </table>
        </div>

        <button
          onClick={logout}
          className="bg-gray-800 text-white px-4 py-2 rounded-md hover:bg-gray-700 transition-colors duration-300"
        >
          Log Out
        </button>
      </div>
    </div>
  );
};

export default AdminHome;
