import React, { useEffect, useState } from "react";
import Table from "./Table";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faSearch, faTrash } from "@fortawesome/free-solid-svg-icons";

const AdminHome = () => {
  const [data, setData] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");

  useEffect(() => {
    fetch(`http://localhost:5050/getAllUser?search=${searchQuery}`, {
      method: "GET",
    })
      .then((res) => res.json())
      .then((data) => {
        console.log(data, "userData");
        setData(data.data);
      });
  }, [searchQuery]);

  const logout = () => {
    window.localStorage.clear();
    window.location.href = "./";
  };

  function handleSearch(e) {
    setSearchQuery(e.target.value);
  }

  return (
    <div className="bg-zinc-400 min-h-screen flex flex-col items-center">
      <div className="bg-white rounded-lg shadow-black/30 border-solid border-2 border-black p-10 w-full max-w-lg">
        <input
          type="search"
          className="w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:border-blue-500 mb-4"
          placeholder="Search"
          onChange={handleSearch}
        />
        <div className="overflow-x-auto w-full">
          {data.map((userData) => (
            <Table key={userData._id} userData={userData} />
          ))}
        </div>
        <button
          onClick={logout}
          className="bg-slate-900 w-20 h-7 text-white rounded-full mt-4"
        >
          Log Out
        </button>
      </div>
    </div>
  );
};

export default AdminHome;
