    reader.readAsDataURL(e.target.files[0]);
    reader.onload = () => {
      console.log(reader.result);
    };
    reader.onerror("Error", error);
  };

  return (
    <div>
      <input accept="image/*" type="file" onChange={convertToImage} />
    </div>
  );
};

export default UploadImage;
