import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";

const UpdateUser = () => {
  const location = useLocation();
  const [fname, setFname] = useState("");
  const [lname, setLname] = useState("");
  const [email, setEmail] = useState("");

  useEffect(() => {
    console.log(location);
    setFname(location.state.fname);
    setLname(location.state.lname);
    setEmail(location.state.Email);
  }, []);

  const updateData = () => {
    console.log(fname, lname);
    fetch("http://localhost:5050/updateUser", {
      method: "POST",
      crossDomain: true,
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
        "Access-Control-Allow-Origin": "*",
      },
      body: JSON.stringify({
        id: location.state._id,
        fname: fname,
        lname: lname,
      }),
    })
      .then((res) => res.json())
      .then((data) => {
        console.log(data, "userdata");
        window.location.href = "/userHome";
      });
    console.log(fname, lname);
  };

  return (
    <div className="bg-zinc-400 h-screen flex justify-center items-center">
      <div className="bg-white rounded-lg shadow-black/30 h-96 pl-10 space-y-3 w-[400px] flex flex-col justify-center">
        <div className="flex flex-col md:flex-row items-center justify-between bg-white-400 border-solid border-2 border-black rounded-lg mr-10 mb-4 p-4 ">
          <div className="flex items-center mb-3 md:mb-0">
            <span className="mr-3">First Name:</span>
            <input
              placeholder="First Name"
              className="outline-none h-10 px-5 border border-sm"
              defaultValue={fname}
              onChange={(e) => setFname(e.target.value)}
            />
          </div>
          <div className="flex items-center mb-3 md:mb-0">
            <span className="mr-3">Last Name:</span>
            <input
              placeholder="Last Name"
              className="outline-none h-10 px-5 border border-sm"
              defaultValue={lname}
              onChange={(e) => setLname(e.target.value)}
            />
          </div>
          <div className="flex items-center mb-3 md:mb-0">
            <span className="mr-3">Email:</span>
            <input
              placeholder="Email"
              className="outline-none h-10 px-5 border border-sm"
              disabled
              defaultValue={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>
          <button
            onClick={updateData}
            className="ml-3 bg-gray-600 rounded-lg pb-2 pt-1 mr-2 pr-1 pl-1 border-solid border-2 border-black"
          >
            Update Details
          </button>
        </div>
      </div>
    </div>
  );
};

export default UpdateUser;
