import React, { useEffect, useState } from "react";
import Table from "./Table";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faSearch, faTrash } from "@fortawesome/free-solid-svg-icons";

const AdminHome = () => {
  const [data, setData] = useState([]);
  const [searchQuery, setSearchQuery] = useState(" ");

  useEffect(() => {
    fetch("http://localhost:5050/getAllUser?search=${searchQuery}", {
      method: "GET",
    })
      .then((res) => res.json())
      .then((data) => {
        console.log(data, "userData");
        setData(data.data);
      });
  }, []);

  const logout = () => {
    window.localStorage.clear();
    window.location.href = "./";
  };

  function handleSearch(e) {
    setSearchQuery(e.target.value);
  }

  return (
    <div className="bg-zinc-400 min-h-screen flex justify-center items-center">
      <div className="bg-white rounded-lg shadow-black/30 border-solid border-2 border-black p-10 space-y-3 flex flex-col justify-center items-center">
        <div className="overflow-x-auto flex-1">
          <div style={{ position: "relative", marginBottom: 10 }}>
            <FontAwesomeIcon
              icon={faSearch}
              style={{
                position: "absolute",
                left: 10,
                top: 13,
                color: "black",
              }}
            />
            <input
              type="text"
              placeholder="Search..."
              onChange={handleSearch}
              style={{
                padding: "8px 32px 8px 32px",
                borderRadius: "4px",
                border: "1px solid #ccc",
                width: "100%",
                boxSizing: "border-box",
              }}
            />
            <span
              style={{ position: "absolute", right: 10, top: 8, color: "#aaa" }}
            >
              {searchQuery.length > 0
                ? `Records Found ${data.length}`
                : `Total Records ${data.length}`}
            </span>
          </div>

          {data.map((userData) => (
            <Table key={userData._id} userData={userData} />
          ))}
        </div>

        <button
          onClick={logout}
          className="bg-slate-900 w-20 h-7 text-white rounded-full"
        >
          Log Out
        </button>
      </div>
    </div>
  );
};

export default AdminHome;
