import React from "react";
import { useEffect } from "react";

const UserHome = ({ userData }) => {
  const logout = () => {
    window.localStorage.clear();
    window.location.href = "./";
  };

  useEffect(() => {
    fetch("http://localhost:5050/userData", {
      method: "POST",
      crossDomain: true,
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
        "Access-Control-Allow-Origin": "*",
      },
      body: JSON.stringify({
        token: window.localStorage.getItem("token"),
      }),
    })
      .then((res) => res.json())
      .then((data) => {
        console.log(data, "userdata");

        setUserData(data.data);
        if (data.data == "token expired") {
          alert("Token expired login again");
          window.localStorage.clear();
          window.location.href = "./";
        }
      });
  }, []);

  return (
    <div>
      <div className="bg-zinc-400 h-screen flex justify-center items-center">
        <div className="bg-white rounded-lg shadow-black/30 h-96 pl-10 space-y-3 w-[350px] flex flex-col justify-center">
          {userData && (
            <div className="bg-white-400 border-solid border-2 border-black rounded-lg mr-10 mb-20">
              Name:<h1> {userData.fname}</h1>
              Email:<h1> {userData.Email}</h1>
              <br />
            </div>
          )}
          <button
            onClick={logout}
            className="flex justify-center items-center bg-slate-900 w-20 h-7 text-white rounded-full mb-2 ml-2"
          >
            Log Out
          </button>
        </div>
      </div>
    </div>
  );
};

export default UserHome;
