import React, { useState } from "react";

const UploadImage = () => {
  const [image, setImage] = useState("");

  const convertToBase64 = (file) =>
    new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.readAsDataURL(file);
      reader.onload = () => resolve(reader.result);
      reader.onerror = (error) => reject(error);
    });

  const convertToImage = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    try {
      const base64 = await convertToBase64(file);
      setImage(base64.replace(/^data:image\/[a-z]+;base64,/, ""));
    } catch (error) {
      console.error("Error converting image to base64:", error);
    }
  };

  const uploadImage = () => {
    fetch("http://localhost:5050/upload-image", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        base64: image,
      }),
    })
      .then((res) => res.json())
      .then((data) => console.log(data))
      .catch((error) => console.error("Error uploading image:", error));
  };

  return (
    <div className="flex items-center mr-4">
      <input accept="image/*" type="file" onChange={convertToImage} />
      {image === "" ? null : (
        <img className="w-[100px] h-[100px]" src={image} alt="Uploaded" />
      )}
      <button onClick={uploadImage} disabled={!image}>
        Upload
      </button>
    </div>
  );
};

export default UploadImage;
